import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

let curDate = new Date();
curDate = curDate.getHours();

let greeting = "";
const cssStyle = {
  // green:'##008000'
  // orange:'#ffa500'
  // black:'#0'
};

// if (curDate >= 1 && curDate < 12) {
